let token=localStorage.getItem("token"),btnUpdateEmail=document.querySelector(".btn--profile").addEventListener("click",e=>{let t=document.getElementById("email").value,n=document.getElementById("skill").value;if(""==t&&""==n){let e=document.querySelector(".alert");e.textContent="Fill in the form",e.classList.remove("hidden")}else fetch("https://thebestchatappever.herokuapp.com/users/profile",{method:"put",headers:{"Content-Type":"application/json",Authorization:"Bearer"+localStorage.getItem("token")},body:JSON.stringify({username:t,skill:n,token:token})}).then(e=>e.json()).then(e=>{if("succes"===e.status){let e=document.querySelector(".alert");e.textContent="Updated",e.classList.remove("hidden")}else{let e=document.querySelector(".alert");e.textContent="Update failed",e.classList.remove("hidden")}})});window.onload=function(){fetch(`https://thebestchatappever.herokuapp.com/users/get/${token}`).then(function(e){return e.json()}).then(function(e){let t=e.data[0].skills,n="";for(let e=0;e<t.length;e++)n+="<li>"+t[e]+"</li>",document.querySelector(".display--skills").innerHTML=n})};